/**
 * Calculates weighted average using nested reduce functions
 * @param {number[]} values - Array of numeric values
 * @param {number[]} weights - Array of corresponding weights
 * @returns {number} Weighted average
 */
function calculateWeightedAverage(values, weights) {
  /**
   * @inner
   * @param {number} sum
   * @param {number} weight
   * @returns {number}
   */
  const totalWeight = weights.reduce((sum, weight) => sum + weight, 0);

  /**
   * @inner
   * @param {number} acc
   * @param {number} value
   * @param {number} index
   * @returns {number}
   */
  const weightedSum = values.reduce((acc, value, index) => {
    return acc + value * weights[index];
  }, 0);

  return weightedSum / totalWeight;
}

/**
 * Creates a counter with private state using closure
 * @returns {Object} Counter with increment and value methods
 */
function createCounter() {
  let count = 0;

  /**
   * @inner
   * Increments the internal counter
   */
  function increment() {
    count++;
  }

  /**
   * @inner
   * @returns {number} Current count value
   */
  function getValue() {
    return count;
  }

  return { increment, getValue };
}

/**
 * Formats a Date object to Persian calendar string
 * @param {Date} date - Input date
 * @returns {string} Formatted string like '1402/05/10'
 * @example
 *   formatToPersian(new Date('2023-07-22')) // returns '1402/05/10'
 */
function formatToPersian(date) {
  const options = { year: 'numeric', month: '2-digit', day: '2-digit' };
  return new Intl.DateTimeFormat('fa-IR', options)
    .format(date)
    .replace(/\/g, '/');
}

/**
 * Validates email format
 * @param {string} email
 * @returns {boolean}
 * @throws {TypeError} If email is not a string
 * @example
 *   isValidEmail('user@example.com') // true
 *   isValidEmail(123) // throws TypeError
 */
function isValidEmail(email) {
  if (typeof email !== 'string') {
    throw new TypeError('Email must be a string');
  }
  const regex = /^[^@]+@[^@]+\.[^@]+$/;
  return regex.test(email);
}

