/**
 * Fetches data with error handling
 * @template T
 * @param {string} url
 * @returns {Ref<T | null>}
 */
export function useFetch<T>(url: string) {
  const data = ref<T | null>(null);
  const error = ref<string | null>(null);

  fetch(url)
    .then(res => res.json())
    .then(json => data.value = json)
    .catch(err => error.value = err.message);

  return { data, error };
}

