 * @property {() => void} toggleMode
 */

/**
 * @context ThemeContext
 * @description Provides theme mode and toggle function
 * @default { mode: 'light', toggleMode: () => {} }
 */
export const ThemeContext = createContext(/** @type {ThemeContextType} */ ({
  mode: 'light',
  toggleMode: () => {}
}));

export function ThemeProvider({ children }) {
  const [mode, setMode] = useState('light');

  const toggleMode = () => {
    setMode(prev => prev === 'light' ? 'dark' : 'light');
  };

  return (
    <ThemeContext.Provider value={{ mode, toggleMode }}>
      {children}
    </ThemeContext.Provider>
  );
}

/**
 * Catches rendering errors in children and shows fallback UI
 * @class
 * @example
 *   <ErrorBoundary>
 *     <ProblematicComponent />
 *   </ErrorBoundary>
 */
class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError() {
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    // Log to service (e.g., Sentry)
    console.error('Error caught by boundary:', error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      return <div>Something went wrong.</div>;
    }

    return this.props.children;
  }
}

