 */
class Logger {
  /**
   * @param {Object} options
   * @param {string} [options.prefix='']
   * @param {'debug'|'info'|'warn'|'error'} [options.level='info']
   */
  constructor({
    prefix = '',
    level = 'info'
  }: {
    prefix?: string;
    level?: 'debug' | 'info' | 'warn' | 'error';
  }) {
    this.prefix = prefix;
    this.level = level;
  }

  log(level: string, message: string) {
    console.log(`[${level}] ${this.prefix}: ${message}`);
  }
}

